/*产品表*/
CREATE TABLE mf_product(
    pid INT PRIMARY KEY AUTO_INCREMENT,/*产品id*/
    title1 VARCHAR(64), /*一级标题*/
    title2 VARCHAR(64), /*二级标题*/
    pic VARCHAR(64),    /*主图片*/
    type INT,           /*类型：1-净水机，2-热水器*/
    model VARCHAR(64),  /*型号*/
    func VARCHAR(64),    /*功能*/
    noise VARCHAR(64),  /*噪音水平*/
    wind VARCHAR(64),   /*风量*/
    applyTO VARCHAR(64),/*适用对象*/
    size VARCHAR(64),   /*使用面积*/
    level VARCHAR(64),  /*能效等级*/
    price FLOAT(10,2),   /*价格*/
    detail VARCHAR(64) /*详情*/
);
