(102,2);

/*购物车详情表*/
CREATE TABLE mf_cart_detail(
    did INT PRIMARY KEY AUTO_INCREMENT,
    cartId  INT,    /*购物车id*/
    productId INT,  /*产品id*/
    count INT       /*数量*/
);
